import asyncio
from datetime import datetime, timedelta

def init(being_context=None):
    """Inicjalizuje rejestr jako singleton"""
    print(f"ðŸ›ï¸ Registry initialized: {being_context.get('alias')}")
    return {'registry_ready': True, 'singleton': True}

async def execute(action=None, being_ulid=None, ttl_hours=24, being_context=None):
    """ZarzÄ…dza rejestrem bytÃ³w"""
    from luxdb.models.being import Being

    registry_data = being_context.get('data', {})
    registered = registry_data.get('registered_beings', {})
    ttl_cache = registry_data.get('ttl_cache', {})

    if action == 'register':
        # Zarejestruj byt z TTL
        expiry = (datetime.now() + timedelta(hours=ttl_hours)).isoformat()
        registered[being_ulid] = {'status': 'active', 'registered_at': datetime.now().isoformat()}
        ttl_cache[being_ulid] = expiry
        print(f"ðŸ“ Registered being {being_ulid[:8]}... with TTL {ttl_hours}h")
        return {'registered': True, 'being_ulid': being_ulid, 'expires_at': expiry}

    elif action == 'get_or_wake':
        # SprawdÅº czy byt jest dostÄ™pny, jeÅ›li nie - obudÅº go
        if being_ulid in registered:
            # SprawdÅº TTL
            if being_ulid in ttl_cache:
                expiry = datetime.fromisoformat(ttl_cache[being_ulid])
                if datetime.now() > expiry:
                    # TTL wygasÅ‚ - usuÅ„ z rejestru
                    del registered[being_ulid]
                    del ttl_cache[being_ulid]
                    print(f"â° Being {being_ulid[:8]}... TTL expired, removed from registry")
                else:
                    print(f"âœ… Being {being_ulid[:8]}... found in registry")
                    return {'found': True, 'being_ulid': being_ulid}

        # Byt nie w rejestrze lub TTL wygasÅ‚ - sprÃ³buj go zaÅ‚adowaÄ‡
        try:
            being = await Being._get_by_ulid_internal(being_ulid)
            if being:
                # Auto-rejestracja
                expiry = (datetime.now() + timedelta(hours=ttl_hours)).isoformat()
                registered[being_ulid] = {'status': 'active', 'awakened_at': datetime.now().isoformat()}
                ttl_cache[being_ulid] = expiry
                print(f"ðŸŒ… Being {being_ulid[:8]}... awakened and registered")
                return {'awakened': True, 'being_ulid': being_ulid, 'being': being}
            else:
                return {'error': 'being_not_found', 'being_ulid': being_ulid}
        except Exception as e:
            return {'error': str(e), 'being_ulid': being_ulid}

    elif action == 'cleanup':
        # WyczyÅ›Ä‡ wygasÅ‚e TTL
        now = datetime.now()
        expired = []
        for ulid, expiry_str in ttl_cache.copy().items():
            if now > datetime.fromisoformat(expiry_str):
                expired.append(ulid)
                del registered[ulid]
                del ttl_cache[ulid]
        print(f"ðŸ§¹ Cleaned up {len(expired)} expired beings")
        return {'cleaned_up': len(expired), 'expired_beings': expired}

    return {'action': action, 'registry_size': len(registered)}
