async def create_being_by_alias(soul_alias, attributes=None, persistent=True, being_context=None):
    """Tworzy Being na podstawie aliasu Soul - delegacja od Being.create()"""
    print(f"ğŸ›ï¸ Registry creating being from soul alias: {soul_alias}")

    from luxdb.models.soul import Soul
    from luxdb.models.being import Being

    # ZnajdÅº Soul po aliasie
    soul = await Soul.get_by_alias(soul_alias)
    if not soul:
        raise ValueError(f"Soul with alias '{soul_alias}' not found")

    # UtwÃ³rz Being bezpoÅ›rednio (bez alias!)
    being = await Being._create_internal(
        soul=soul, 
        attributes=attributes,
        persistent=persistent
    )

    # Zarejestruj w registry
    await register_being(being.ulid, {
        "soul_alias": soul_alias,
        "soul_hash": soul.soul_hash,
        "created_by_registry": True
    }, being_context)

    return {
        "being": being.to_json_serializable(),
        "registered": True,
        "soul_alias": soul_alias
    }

def register_being(being_ulid, being_info=None, being_context=None):
    """Rejestruje nowy byt w registry"""
    print(f"ğŸ“ Registering being {being_ulid}")

    managed_beings = being_context.get('data', {}).get('managed_beings', [])
    managed_beings.append({
        "ulid": being_ulid,
        "registered_at": "2025-01-30T00:00:00",
        "info": being_info or {}
    })

    return {
        "registered": True,
        "being_ulid": being_ulid,
        "total_managed": len(managed_beings)
    }

def get_being_info(being_ulid, being_context=None):
    """Pobiera informacje o zarejestrowanym bycie"""
    managed_beings = being_context.get('data', {}).get('managed_beings', [])

    for being_info in managed_beings:
        if being_info.get('ulid') == being_ulid:
            return being_info

    return None

