def execute(source_ulid=None, target_ulid=None, action='check', being_context=None):
    """Zarządza relacjami can_use"""
    if action == 'check':
        return {'can_use': True, 'relation_exists': True}
    elif action == 'create':
        return {'relation_created': True, 'source': source_ulid, 'target': target_ulid}
    return {'action': action, 'result': 'executed'}
